import React, { useState } from 'react';
import '../assets/program.css';
import mba from '../assets/img/mba.png';
import mbahr from '../assets/img/mbahr.png';
import pgpbm from '../assets/img/pgpbm.png';
import phd from '../assets/img/phd.png';
import exphd from '../assets/img/exphd.png';

const programs = [
  {
    category: 'BE',
    programs: [
      { name: 'CSE', image: mba, description: 'The Four-years full-time under Graduate Programme in Management (PGPM) is the flagship programme of BARGUR ENGINEERING COLLEGE .' },
      { name: 'ECS', image: mbahr, description: 'Post-Graduate Programme in Management-Human Resources (PGPM-HR) is a two-year full-time residential programme launched in 2020.' },
    ],
  },
  {
    category: 'BE',
    programs: [
      { name: "EEE", image: pgpbm, description: 'IIM Tiruchirappalli launched the PGPBM programme at its Chennai campus to groom working executives who aspire to move into senior leadership roles.' },
    ],
  },
  {
    category: 'BE',
    programs: [
      { name: 'MECH', image: phd, description: 'The Doctoral Programme in Management is a full-time doctoral programme that offers students opportunities for research in different areas of Management.' },
    ],
  },
  {
    category: 'BE',
    programs: [
      { name: 'CIVIL', image: exphd, description: 'IM Tiruchirappalli’s Executive Ph.D. is a non-residential doctoral programme designed for senior executives for academic research' },
    ],
  },
];

const Programs = () => {
  const [selectedProgram, setSelectedProgram] = useState({
    category: 'PGPM',
    program: "MBA",
    image: mba,
    description: 'The two-year full-time Post Graduate Programme in Management (PGPM) is the flagship programme of IIM Tiruchirappalli',
  });

  const handleProgramClick = (category, program, image, description) => {
    setSelectedProgram({ category, program, image, description });
  };

  return (
    <div className="programmes-container">
      <h1 className="main-heading"> Bargur College Programmes Highlights</h1>
      <div className="programmes-sidebar">
        {programs.map((section) => (
          <div key={section.category}>
            <div className="programmes-category">{section.category}</div>
            <ul>
              {section.programs.map((prog) => (
                <li
                  key={prog.name}
                  className={selectedProgram.program === prog.name ? 'active' : ''}
                  onClick={() =>
                    handleProgramClick(section.category, prog.name, prog.image, prog.description)
                  }
                >
                  {prog.name}
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
      <div className="programmes-content">
        <img src={selectedProgram.image} alt={selectedProgram.program} className="programmes-image" />
        <div className="programmes-text">
          <h2>B.E. (MECHANICAL ENGINEERING)  </h2>
          <p>{selectedProgram.description}</p>
          <div className="programmes-buttons">
            <button className="read-more-button">READ MORE</button>
            <button className="admission-button">ADMISSION</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Programs;
